function AuthenticatedTest(outputs, steps, params, stepResult, assertEqual) {
	var groupUtils = x_912467_klf.GroupUtils;
	var testUtils = global.KLF_TestUtils;

	describe('Authenticated.isMemberOf', function() {
		var originalUserSysId = '';
		/** @type {GlideRecord} */
		// @ts-ignore 
		var group = null; 
		/** @type {GlideRecord} */
		// @ts-ignore 
		var user = null;
		beforeAll(function() {
			group = groupUtils.createGroup('test-group');
			user = groupUtils.createUser('test-user');
		});

		afterAll(function() {
			// Go back to the original user
			testUtils.impersonateUser(originalUserSysId);

			// Delete the group and user
			if(group.isValidRecord()) {
				group.deleteRecord();
			}
			if(user.isValidRecord()) {
				user.deleteRecord();
			}
		});

		it('should return true if the user is a member of the specified group', function() {
			// impersonate the user
			originalUserSysId = testUtils.impersonateUser(user.getUniqueValue());
			expect(user.getUniqueValue()).toEqual(gs.getUserID());
		});
	});

}
