sys_script_include.script:
/**
 * Object that represents the currently authenticated user.
 */
const Authenticated = (function() {
    const groupUtils = x_912467_klf.GroupUtils;

    class Authenticated {
        constructor() { }

        /**
         * Returns the sys_user.sys_id of the currently authenticated user.
         * @returns {string} sys_user.sys_id of the currently authenticated user
         */
        getUserSysId() {
            return gs.getUserID();
        }

        /**
         * Returns true if the user is a member of the specified group.
         * @param {string} groupSysId sys_id of the group to check
         */
        isMemberOf(groupSysId) {
            return groupUtils.isMemberOf(this.getUserSysId(), groupSysId);
        }

    }

    return new Authenticated();
})();
