function TestTaskManagerTest(outputs, steps, params, stepResult, assertEqual) {
    describe('TaskManager.canWrite', function() {
        var testUtils = global.KLF_TestUtils;

        var task = null;
        beforeEach(function() {
            // Create a task
            testUtils.impersonateCommonUser();
            task = getSubmittedTask();
        });

        afterEach(function() {
            testUtils.impersonateAdminUser();
            testUtils.deleteRecordsCreatedByCommonUser('x_912467_klf_todo_task');
        });

        it('should return true if user is assigned to task', function() {

        });

        it('should return false if user didnt create task', function() {

        });

        /**
         * Creates a non-persisted todo that has all the required fields
         * set but has not been persisted
         * @returns {GlideRecord} x_912467_klf_todo_task
         */
        function createTask() {
            var todo = new GlideRecord('x_912467_klf_todo_task');
            todo.newRecord();
            todo.label = 'Test Task';
            return todo;
        }

        /**
         * Creates a persisted task that represents a task that has
         * been submitted
         * @returns {GlideRecord} x_912467_klf_todo_task
         */
        function getSubmittedTask() {
            var todo = createTask();
            TaskManager.submit(todo);
            return todo;
        }
    });
}
